mapper.select:
select regist.REG_TIME, sum(regist.SCAN_COUNT) cot from ITV_REGIST regist, hospital h, doctor_detail dd
h.address_id in ( select id from address a
find_in_set( pid, fn_getaddress_childlist (#{mCodeValue}) ) or code_value = #{mCodeValue}
) and h.id = dd.zp_hospital_id and regist.DOCTOR_ID = dd.doctor_id
group by to_days(REG_TIME)

select regist.REG_TIME, sum(regist.SCAN_COUNT) cot from ITV_REGIST regist, hospital h, doctor_detail dd
h.address_id in ( select id from address a
find_in_set( pid, fn_getaddress_childlist (#{mCodeValue}) ) or code_value = #{mCodeValue}
) and h.id = dd.zp_hospital_id and regist.DOCTOR_ID = dd.doctor_id and regist.reg_state = #{mNum}
group by to_days(REG_TIME)
